import React, { useState } from 'react'
import product from './data/products';

const App = () => {
  const [data,setData] = useState(product);
  return (
    <div className='container mt-5'>

    {data.map((fd,i)=>(
          <ul key={i}>
          <li>{fd.title}</li>
          <li>{fd.price}</li>
          <li>{fd.color}</li>
          </ul>
        ))}

    </div>
  )
}

export default App
