import React, { useEffect, useState } from "react";
import u from './data/user'

const App = () => {
  const [alert, setAlert] = useState();
  const [color, setColor] = useState();
  const [user, setUser] = useState();
  const [pass, setPass] = useState();


  useEffect(()=>{
    document.querySelector('input').onclick=()=>{
      bacolo
    }
  })


  const formSubmit = (e) => {
    e.preventDefault();
    if (!user || !pass) {
      setColor("alert-warning");
      setAlert("fill out input");
    }else{
      if (user === u.username && pass === u.pass) {
        setColor("alert-success");
        setAlert("success");
      }else{
        setColor("alert-danger");
        setAlert("err");
      }
    }
  };

  return (
    <form onSubmit={formSubmit} className="container mt-5">
      <div>
        <p className={`alert ${color}`}>{alert}</p>
      </div>
      <input
        onChange={(e) => setUser(e.target.value)}
        placeholder="enter username"
        type="text"
      />
      <br />
      <input  onChange={(e) => setPass(e.target.value)} placeholder="enter password" type="text" />
      <br />
      <button className="btn btn-primary">try</button>
    </form>
  );
};

export default App;
